async_workers_group="name=abc;workers=4;nonblock=0;usleep=0"
...
request_route {
    ...
    async_task_data("RESUME", "caller: $fU - callee: $tU");
    ...
}
route[RESUME] {
   xinfo("$async(data)\n");
   exit;
}
