route[MYREQ]
{
...
    if (!t_newtran()) {
        xlog("L_ERROR", "$ci|log|failed to create transaction\n");
        drop;
    }

    t_on_failure("TR_ERROR");
    t_on_reply("TR_OK");
    t_relay();
}

failure_route[TR_ERROR]
{
    xlog("L_INFO", "$ci|log|failed $T_reply_code $T_reply_reason\n");
    t_drop();
}

onreply_route[TR_OK]
{
   xlog("L_INFO", "$ci|log|checking transaction result\n");
   if(status=~"60[0-9]") {
      t_drop();
   }
...
}
